import React, { Component } from 'react';

class Resume extends Component {
  render() {
    if (!this.props.data) {
      return <div>Loading...</div>;
    }

    const { education, work, certifications } = this.props.data;

    const educationList = education.map((edu) => (
      <div key={edu.school}>
        <h3>{edu.school}</h3>
        <p className="info">
          {edu.degree} <span>&bull;</span>
          <em className="date">{edu.graduated}</em>
        </p>
        <p>{edu.description}</p>
      </div>
    ));

    const workList = work.map((job) => (
      <div key={job.company}>
        <h3>{job.company}</h3>
        <p className="info">
          {job.title} <span>&bull;</span>
          <em className="date">{job.years}</em>
        </p>
        <p>{job.description}</p>
      </div>
    ));

    const certificationsList = certifications.map((cert) => (
      <div key={cert.title}>
        <h3>{cert.title}</h3>
        <p className="info">
          {cert.organization} <span>&bull;</span>
          <em className="date">{cert.date}</em>
        </p>
      </div>
    ));

    return (
      <section id="resume">
        <div className="row work">
          <div className="three columns header-col">
            <h1>
              <span>Work Experience</span>
            </h1>
          </div>
          <div className="nine columns main-col">{workList}</div>
        </div>

        <div className="row certifications">
          <div className="three columns header-col">
            <h1>
              <span>Certifications</span>
            </h1>
          </div>
          <div className="nine columns main-col">{certificationsList}</div>
        </div>

        <div className="row education">
          <div className="three columns header-col">
            <h1>
              <span>Education</span>
            </h1>
          </div>
          <div className="nine columns main-col">{educationList}</div>
        </div>
      </section>
    );
  }
}

export default Resume;
