import React, { Component } from 'react';
import Modal from 'react-modal';

import '../App.css'

class Portfolio extends Component {

  state = {
    isOpen: false,
    currentProject: null,
  };

  openModal = (project) => {
    this.setState({ isOpen: true, currentProject: project });
  };

  closeModal = () => {
    this.setState({ isOpen: false, currentProject: null });
  };

  render() {

    if(this.props.data){
      var projects = this.props.data.projects.map(function(projects){
        var projectImage = 'images/portfolio/'+projects.image;

        return <div key={projects.title} className="columns portfolio-item">
           <div className="item-wrap">
            <a href={projects.url} title={projects.title}>
               <img alt={projects.title} src={projectImage} />
               <div className="overlay">
                  <div className="portfolio-item-meta">
                 <h5>{projects.title}</h5>
                     <p>{projects.category}</p>
                  </div>
                </div>
              <div className="link-icon"><i className="fa fa-link"></i></div>
            </a>
          </div>
        </div>
      })
    }

    return (
      <section id="portfolio">
        <div className="row">
          <div className="twelve columns collapsed">
            <h1>Check Out Some of My Works.</h1>
            <div
              id="portfolio-wrapper"
              className="bgrid-quarters s-bgrid-thirds cf"
            >
              {projects}
            </div>
          </div>
        </div>

        {this.state.currentProject && (
          <Modal
            isOpen={this.state.isOpen}
            onRequestClose={this.closeModal}
            contentLabel="Project Details"
            className="modal"
            overlayClassName="overlay"
          >
            <h2>{this.state.currentProject.title}</h2>
            <p>{this.state.currentProject.description}</p>
            <a
              href={this.state.currentProject.url}
              target="_blank"
              rel="noopener noreferrer"
            >
              Visit Project
            </a>
            <button onClick={this.closeModal}>Close</button>
          </Modal>
        )}
      </section>
    );
  }
}

export default Portfolio;
