import React, { Component } from 'react';

class About extends Component {
  render() {
    if (this.props.data) {
      var name = this.props.data.name;
      var profilepic = "images/" + this.props.data.image;
      var bio = this.props.data.bio;
      var street = this.props.data.address.street;
      var city = this.props.data.address.city;
      var state = this.props.data.address.state;
      var zip = this.props.data.address.zip;
      var phone = this.props.data.phone;
      var email = this.props.data.email;
      var resumeDownload = this.props.data.resumedownload;
    }

    return (
      <section id="about">
      <div class="profile-pic-wrapper">
        <img class="profile-pic" src={profilepic} alt="Keerthi Swaroop's Profile Pic" />
      </div>
      <div class="main-col">
        <h2>About Me</h2>
        <p>
          My name is Keerthi Swaroop Satambakkam. I'm a skilled Data Engineer with expertise in Snowflake,
          Apache Airflow, and modern ETL/ELT workflows. I specialize in building scalable and efficient data pipelines.
        </p>
        <div class="contact-details">
          <h2>Contact Details</h2>
          <p class="address">
            <span>Keerthi Swaroop Satambakkam</span><br />
            <span>4211 Ridge Top Road<br />Fairfax, VA, 22030</span><br />
            <span>+1 (609)-464-4479</span><br />
            <span>ksatamba1103@gmail.com</span>
          </p>
        </div>
        <div class="download">
          <a href="path-to-your-resume.pdf" class="button">
            <i class="fa fa-download"></i> Download Resume
          </a>
        </div>
      </div>
    </section>
    );
  }
}

export default About;
